import {GelatoRelayContext} from "@gelatonetwork/relay-context/contracts/GelatoRelayContext.sol";

abstract contract Claimable is GelatoRelayContext{
    bool public gelatoRelayEnabled;

    address private defaultCertificateAuthority;
    mapping(address => bool) private usedSigner;

    error UsedSigner();
    error ZeroAddress();
    error InvalidCertificate();
    error InvalidSignature();
    error GelatoRelayNotEnabled();

    function claim(address claimant, address signer, uint64 deadline, bytes calldata data, bytes calldata signature, bytes calldata certificate) public {
        bytes32 certificateHash = _verifyCertificate(signer, deadline, data, certificate);
        _verySignature(claimant, certificateHash, signer, signature);

        if(usedSigner[signer]) revert UsedSigner();

        usedSigner[signer] = true;

        _processClaim(claimant, data);
    }

    function claimThroughRelay(address claimant, address signer, uint64 deadline, bytes calldata data, bytes calldata signature, bytes calldata certificate) 
        external onlyGelatoRelay {

        if (!gelatoRelayEnabled) revert GelatoRelayNotEnabled();

        _beforeTransferRelayFee(claimant, data);
        _transferRelayFee();

        claim(claimant, signer, deadline, data, signature, certificate);
    }


    function _processClaim(address claimant, bytes calldata data) internal virtual;


    function _beforeTransferRelayFee(address claimant, bytes calldata data) internal virtual {}

    function isUsedSigner(address signer) public view returns (bool) {
        return usedSigner[signer];
    }

    function getCertificateAuthority() public virtual view returns (address) {
        return defaultCertificateAuthority;
    }

    function _setDefaultCertificateAuthority(address certificateAuthority_) internal {
        if(certificateAuthority_ == address(0)) revert ZeroAddress();
        defaultCertificateAuthority = certificateAuthority_;
    }


    function _verifyCertificate(address signer,  uint64 deadline, bytes memory data, bytes memory certificate) internal view returns (bytes32) {
        bytes32 certificateHash = ECDSAUpgradeable.toEthSignedMessageHash(keccak256(abi.encodePacked(
            signer, deadline,
            address(this), block.chainid,
            data
        )));

        if(deadline < block.timestamp) revert InvalidCertificate();
        if(ECDSAUpgradeable.recover(certificateHash, certificate) != getCertificateAuthority()) revert InvalidCertificate();

        return certificateHash;
    }

    function _verySignature(address claimant, bytes32 certificateHash, address signer, bytes calldata signature) internal pure {

        bytes32 signautureHash = ECDSAUpgradeable.toEthSignedMessageHash(keccak256(abi.encodePacked(
            claimant, certificateHash
        )));

        if(ECDSAUpgradeable.recover(signautureHash, signature) != signer) revert InvalidSignature();
    }

}","keccak256":"0x239ad09b42cb2d5f00e566fd3c3cd65e91d0af2fd6e09fe0fb0c18703e94d616","license":"MIT"},"contracts/Claimable1155.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/token/ERC1155/ERC1155Upgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import {NATIVE_TOKEN} from  "@gelatonetwork/relay-context/contracts/constants/Tokens.sol";

import "./ClaimableCore.sol";

import "./IFactory.sol";


contract Claimable1155 is ClaimableCore, ERC1155Upgradeable {

    string public name;
    string public symbol;

    mapping(uint256 => string) private _uri;

    function initialize(
        string calldata name_,
        string calldata symbol_,
        bool gelatoRelayEnabled_,
        address certificateAuthority_,
        address factory_
    ) initializer external {
        __ERC1155_init("");
        name = name_;
        symbol = symbol_;

        __ClaimableCore_init(gelatoRelayEnabled_, certificateAuthority_, factory_);

    }

    function toggleGelatoRelay() external onlyOwner {
        gelatoRelayEnabled = !gelatoRelayEnabled;
    }

    function _max_mint_gasusage() internal virtual returns(uint256) {
        return 50000;
    }

    function uri(uint256 id) public view override returns(string memory) {
        return _uri[id];
    }

    function setUri(uint256 id, string calldata uri_) external onlyOwner {
        _uri[id] = uri_;
        emit URI(uri_, id);
    }

    function _processClaim(address claimant, bytes calldata data) internal virtual override {
        (uint256 id, uint256 amount) = abi.decode(
            data,
            (uint256, uint256)
        );
        uint256 beforeGas = gasleft();
        _mint(claimant, id, amount, "");
        uint256 afterGas = gasleft();

        if (_isGelatoRelay(msg.sender)) {
            // By capping the gas usage for the mint, this prevents the griefing attack by using a smart contact as the clamant.
            if (beforeGas - afterGas > _max_mint_gasusage()) revert();


            address token = _getFeeToken();

            uint256 fee = IFactory(factory).getFee(token);

            if(fee > 0) {
                if(token == NATIVE_TOKEN) {
                    payable(factory).transfer(fee);
                } else {
                    IERC20(token).transfer(factory, fee);
                }
            }
        } 
    } 
}","keccak256":"0x6faf389c93b4584a5192a79f50419d247d79fa6dc70246e12fefdfc9e5db5473","license":"MIT"},"contracts/ClaimableCore.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "./Claimable.sol";


abstract contract ClaimableCore is Claimable, OwnableUpgradeable {
    address public factory;

    constructor() {
        _disableInitializers();
    }

    function __ClaimableCore_init(
        bool gelatoRelayEnabled_,
        address certificateAuthority_,
        address factory_
    ) internal onlyInitializing {
        gelatoRelayEnabled = gelatoRelayEnabled_;
        _setDefaultCertificateAuthority(certificateAuthority_);
        __Ownable_init();
        factory = factory_;
    }

    function setCertificateAuthority(address certificateAuthority_) external onlyOwner {
        _setDefaultCertificateAuthority(certificateAuthority_);
    }

    receive() external payable {}

    function withdrawERC20(IERC20 token, uint256 amount) external onlyOwner {
        token.transfer(msg.sender, amount);
    }

    function withdraw(uint256 amount) external onlyOwner {
        payable(msg.sender).transfer(amount);
    }
}","keccak256":"0xd4b528fd5e2d7208b80298b66c999a8e3e31ebca50de3e07d3249d4f2c42e3d7","license":"MIT"},"contracts/IFactory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;


interface IFactory {
    function getFee(address token) external view returns (uint256);
}","keccak256":"0x081cb46fbb3c6dea11bd5d66bc80767de52c3a0f72b4c82c8732c350c6b4e5ae","license":"MIT"}},"version":1}
