pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";


import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol";

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "erc721a-upgradeable/contracts/ERC721AUpgradeable.sol";

import "../LinkForgeCore.sol";


contract LinkForge721Demo is LinkForgeCore, ERC721AUpgradeable {
    using StringsUpgradeable for uint256;

    string public baseURI;

    error SoulBound();

    function initialize(
        string calldata name_,
        string calldata symbol_,
        bool gelatoRelayEnabled_,
        address certificateAuthority_
    ) initializerERC721A initializer public {
        __LinkForgeCore_init(
            gelatoRelayEnabled_,
            certificateAuthority_
        );

        __ERC721A_init(name_, symbol_);
        __Ownable_init();
    }

    function _startTokenId() internal pure override returns(uint256) {
        return 1;
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        return string(abi.encodePacked(baseURI, tokenId.toString()));
    }

    function _processClaim(address claimant, bytes calldata) internal override {
        _mint(claimant, 1);
    } 

    function setBaseURI(string calldata baseURI_) public onlyOwner {
        baseURI = baseURI_;
    }

    function _beforeTokenTransfers(
        address from,
        address,
        uint256,
        uint256
    ) internal pure override {
        if(from != address(0)) revert SoulBound();
    }

    function setApprovalForAll(address, bool) public pure override {
        revert SoulBound();
    }

    function getApproved(uint256) public pure override returns (address) {
        revert SoulBound();
    }
