// SPDX-License-Identifier: MIT
// Modified from Openzeppelin contracts

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/interfaces/IERC165.sol";
import "@openzeppelin/contracts/interfaces/IERC2981.sol";



abstract contract ERC2981 is IERC165, IERC2981 {
    struct ERC2981Layout {
        address receiver;
        uint96 feeNumerator;
    }

    bytes32 private constant STORAGE_SLOT = keccak256('CtorLab.contracts.storage.ERC2981');

    error ExceedSalePrice();
    error InvalidReceiver();


    function layout() private pure returns (ERC2981Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }

    function supportsInterface(bytes4 interfaceId) public view virtual returns (bool) {
        return interfaceId == type(IERC2981).interfaceId || 
            interfaceId == type(IERC165).interfaceId;
    }

    /**
    * @dev Sets the royalty information that applies to all ids in this contract.
    *
    * Requirements:
    *
    * - `receiver` cannot be the zero address.
    * - `feeNumerator` cannot be greater than the fee denominator.
    */
    function _setRoyalty(address receiver_, uint96 feeNumerator_) internal virtual {
        if (feeNumerator_ > _feeDenominator()) revert ExceedSalePrice();
        if (receiver_ == address(0)) revert InvalidReceiver();

        layout().receiver = receiver_;
        layout().feeNumerator = feeNumerator_;
    }

    /**
     * @dev For drop-in replacement of Openzeppelin's ERC2981 implemenatation.
     */
    function _setDefaultRoyalty(address receiver_, uint96 feeNumerator_) internal virtual {
        _setRoyalty(receiver_, feeNumerator_);
    }

    /**
     * @dev Returns how much royalty is owed and to whom, based on a sale price that may be denominated in any unit of
     * exchange. The royalty amount is denominated and should be paid in that same unit of exchange.
     */
    function royaltyInfo(uint256 _tokenId, uint256 _salePrice) public view virtual override returns (address, uint256) {
        _tokenId; // Disable warning
        uint256 royaltyAmount = (_salePrice * layout().feeNumerator) / _feeDenominator();
        return (layout().receiver, royaltyAmount);
    }

    /**
     * @dev The denominator with which to interpret the fee set in {_setRoyalty} as a
     * fraction of the sale price. Defaults to 10000 so fees are expressed in basis points, but may be customized by an
     * override.
     */
    function _feeDenominator() internal pure virtual returns (uint96) {
        return 10000;
    }

}

contracts/LinkForge1155.sol.content:
// SPDX-License-Identifier: MIT
// Author: Ctor Lab

pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {NATIVE_TOKEN} from  "@gelatonetwork/relay-context/contracts/constants/Tokens.sol";
import "solady/src/tokens/ERC1155.sol";
import "closedsea/src/OperatorFilterer.sol";


import "./LinkForgeCore.sol";
import "./interfaces/IFactory.sol";
import "./interfaces/ILinkForge1155.sol";

import "./library/ERC2981.sol";


library LinkForge1155Storage {
    bytes32 internal constant STORAGE_SLOT = keccak256('CtorLab.contracts.storage.LinkForge1155');

    struct Layout {
        address factory;
        string name;
        string symbol;
        string defaultURI;
        mapping(uint256 => string) uri;
    }

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

contract LinkForge1155 is LinkForgeCore, ERC1155, ILinkForge1155, ERC2981, OperatorFilterer, UUPSUpgradeable {

    event Claimed(address recepiant, uint256 tokenId, uint256 amount, address token, uint256 fee);

    function initialize(
        string calldata name_,
        string calldata symbol_,
        bool gelatoRelayEnabled_,
        address certificateAuthority_,
        address factory_
    ) initializerERC721A external { 
        LinkForge1155Storage.layout().name = name_;
        LinkForge1155Storage.layout().symbol = symbol_;
        LinkForge1155Storage.layout().factory = factory_;

        __LinkForgeCore_init(gelatoRelayEnabled_, certificateAuthority_);
        _registerForOperatorFiltering();
    }

    function name() public view returns (string memory) {
        return LinkForge1155Storage.layout().name;
    }

    function symbol() public view returns (string memory) {
        return LinkForge1155Storage.layout().symbol;
    }

    function version() public pure returns (uint256) {
        return 1;
    }

    function factory() public view returns (address) {
        return LinkForge1155Storage.layout().factory;
    }

    function devMint(address to, uint256 id, uint256 amount) external onlyOwner {
        _mint(to, id, amount, "");
    }

    function _max_mint_gasusage() internal virtual returns(uint256) {
        return 50000;
    }

    function uri(uint256 id) public view override returns(string memory) {
        if(bytes(LinkForge1155Storage.layout().uri[id]).length > 0) {
            return LinkForge1155Storage.layout().uri[id];
        }
        return LinkForge1155Storage.layout().defaultURI;
    }

    function setUri(uint256 id, string calldata uri_) external payable onlyOwner {
        LinkForge1155Storage.layout().uri[id] = uri_;
        emit URI(uri_, id);

        if(msg.value > 0) {
            payable(factory()).transfer(msg.value);
        }
    }

    function defautURI() public view returns(string memory) {
        return LinkForge1155Storage.layout().defaultURI;
    }

    function setDefaultUri(string calldata defaultURI_) external onlyOwner {
        LinkForge1155Storage.layout().defaultURI = defaultURI_;
    }

    function _processClaim(address recepiant, bytes calldata data) internal virtual override {
        (uint256 id, uint256 amount) = abi.decode(
            data,
            (uint256, uint256)
        );
        uint256 beforeGas = gasleft();
        _mint(recepiant, id, amount, "");
        uint256 afterGas = gasleft();

        address factory_ = factory();
        address token = address(0);
        uint256 fee = 0;

        if (_isGelatoRelay(msg.sender)) {
            // By capping the gas usage for the mint, this prevents the griefing attack by using a smart contact as the clamant.
            if (beforeGas - afterGas > _max_mint_gasusage()) revert();

            token = _getFeeToken();

            if(factory_ != address(0)) {
                fee = IFactory(factory_).getFeeRelayed(token);

                if(fee > 0) {
                    if(token == NATIVE_TOKEN) {
                        payable(factory_).transfer(fee);
                    } else {
                        IERC20(token).transfer(factory_, fee);
                    }
                }
            }
            fee +=  _getFee();
        } else {
            require(msg.value == IFactory(factory_).getFeeSelfClaimed());
            fee = msg.value;
            payable(factory_).transfer(msg.value);
        }

        emit Claimed(recepiant, id, amount, token, fee);
    } 

    function supportsInterface(bytes4 interfaceId) public view virtual override(ERC2981, ERC1155) returns (bool) {
        return ERC2981.supportsInterface(interfaceId) || ERC1155.supportsInterface(interfaceId);
    }

    // UUPS

    function _authorizeUpgrade(address newImplementation) internal override view onlyOwner {
        IFactory(factory()).authorizeUpgrade(newImplementation);
    }

    // ERC2981

    function setRoyalty(address receiver_, uint96 feeNumerator_) external onlyOwner {
        _setRoyalty(receiver_, feeNumerator_);
    }
}

contracts/LinkForgeBase.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// Author: Ctor Lab

pragma solidity ^0.8.9;

import "@openzeppelin/contracts-upgradeable/utils/cryptography/ECDSAUpgradeable.sol";
import {GelatoRelayContext} from "@gelatonetwork/relay-context/contracts/GelatoRelayContext.sol";


int256 constant SIGNER_AVAILABLE = 0;
int256 constant SIGNER_USED = 1;
int256 constant SIGNER_REVOKED = 2;
int256 constant SIGNER_UNLIMITED = type(int256).min;


library LinkForgeBaseStorage {
    bytes32 internal constant STORAGE_SLOT = keccak256('CtorLab.contracts.storage.ClaimableBase');

    struct Layout {
        bool gelatoRelayEnabled;
        address defaultCertificateAuthority;
        mapping(address=> int256) signerState;
    }

    function layout() internal pure returns (Layout storage l) {
        bytes32 slot = STORAGE_SLOT;
        assembly {
            l.slot := slot
        }
    }
}

abstract contract LinkForgeBase is GelatoRelayContext{

    error InvalidSigner();
    error ZeroAddress();
    error InvalidCertificate();
    error InvalidSignature();
    error GelatoRelayNotEnabled();

    event SignerRevoked(address signer);
    event SignerUsed(address signer);

    function claim(address recepiant, address signer, uint64 deadline, bytes calldata data, bytes calldata signature, bytes calldata certificate) public payable {
        bytes32 certificateHash = _verifyCertificate(signer, deadline, data, certificate);
        _verifySignature(recepiant, certificateHash, signer, signature);

        int256 signerState_ = LinkForgeBaseStorage.layout().signerState[signer];

        if(signerState_ > SIGNER_AVAILABLE) {
            revert InvalidSigner();
        } else if(signerState_ == SIGNER_AVAILABLE) {
            LinkForgeBaseStorage.layout().signerState[signer] = SIGNER_USED;
            emit SignerUsed(signer);
        }

        _processClaim(recepiant, data);
    }

    function claimThroughRelay(address recepiant, address signer, uint64 deadline, bytes calldata data, bytes calldata signature, bytes calldata certificate) 
        external onlyGelatoRelay {

        if (!LinkForgeBaseStorage.layout().gelatoRelayEnabled) revert GelatoRelayNotEnabled();

        _beforeTransferRelayFee(recepiant, data);
        _transferRelayFee();

        claim(recepiant, signer, deadline, data, signature, certificate);
    }


    function _processClaim(address recepiant, bytes calldata data) internal virtual;


    function _beforeTransferRelayFee(address recepiant, bytes calldata data) internal virtual {}


    function signerState(address signer) public view returns (int256) {
        return LinkForgeBaseStorage.layout().signerState[signer];
    }

    function getCertificateAuthority() public virtual view returns (address) {
        return LinkForgeBaseStorage.layout().defaultCertificateAuthority;
    }

    function _setDefaultCertificateAuthority(address certificateAuthority_) internal {
        if(certificateAuthority_ == address(0)) revert ZeroAddress();
        LinkForgeBaseStorage.layout().defaultCertificateAuthority = certificateAuthority_;
    }

    function gelatoRelayEnabled() public view returns(bool){
        return LinkForgeBaseStorage.layout().gelatoRelayEnabled;
    }


    function _verifyCertificate(address signer,  uint64 deadline, bytes memory data, bytes memory certificate) internal view returns (bytes32) {
        bytes32 certificateHash = ECDSAUpgradeable.toEthSignedMessageHash(keccak256(abi.encodePacked(
            signer, deadline,
            address(this), block.chainid,
            data
        )));

        if(deadline < block.timestamp) revert InvalidCertificate();
        if(ECDSAUpgradeable.recover(certificateHash, certificate) != getCertificateAuthority()) revert InvalidCertificate();

        return certificateHash;
    }

    function _verifySignature(address recepiant, bytes32 certificateHash, address signer, bytes calldata signature) internal pure {

        bytes32 signautureHash = ECDSAUpgradeable.toEthSignedMessageHash(keccak256(abi.encodePacked(
            recepiant, certificateHash
        )));

        if(ECDSAUpgradeable.recover(signautureHash, signature) != signer) revert InvalidSignature();
    }

    function _revoke(address signer) internal {
        LinkForgeBaseStorage.layout().signerState[signer] = SIGNER_REVOKED;
        emit SignerRevoked(signer);
    }
}

contracts/LinkForgeCore.sol.content:
// SPDX-License-Identifier: BUSL-1.1
// Author: Ctor Lab

pragma solidity ^0.8.9;


import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "solady/src/auth/Ownable.sol";

import "./LinkForgeBase.sol";
import "erc721a-upgradeable/contracts/ERC721A__Initializable.sol";

abstract contract LinkForgeCore is LinkForgeBase, Ownable, ERC721A__Initializable {

    function __LinkForgeCore_init(
        bool gelatoRelayEnabled_,
        address certificateAuthority_
    ) internal onlyInitializingERC721A {
        LinkForgeBaseStorage.layout().gelatoRelayEnabled = gelatoRelayEnabled_;
        _setDefaultCertificateAuthority(certificateAuthority_);
        _initializeOwner(msg.sender);
    }

    function toggleGelatoRelayEnabled() external onlyOwner {
        LinkForgeBaseStorage.layout().gelatoRelayEnabled = !LinkForgeBaseStorage.layout().gelatoRelayEnabled;
    }

    function setCertificateAuthority(address certificateAuthority_) external onlyOwner {
        _setDefaultCertificateAuthority(certificateAuthority_);
    }

    function setUnlimitedSigner(address signer) external onlyOwner {
        if(LinkForgeBaseStorage.layout().signerState[signer] != SIGNER_AVAILABLE) revert InvalidSigner();
        LinkForgeBaseStorage.layout().signerState[signer] = SIGNER_UNLIMITED;
    }

    function revokeSigners(address[] calldata signers) external onlyOwner {
        for(uint256 i=0;i<signers.length;) {
            _revoke(signers[i]);
            unchecked {
                ++i;
            }
        }
    }

    receive() external payable {}

    function withdrawERC20(IERC20 token, uint256 amount) external onlyOwner {
        token.transfer(msg.sender, amount);
    }

    function withdraw(uint256 amount) external onlyOwner {
        payable(msg.sender).transfer(amount);
    }
}

